import React from "react";
import "./liked-icon.styles.scss";
import { ReactComponent as Heart } from "../../assets/heart.svg";
import { selectLikedItemToggle } from "../../redux/liked/liked.selectors";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

const LikedIcon = ({ inverted, toggleLikedItem, ...otherProps }) => {
  return (
    <div className="liked-icon" {...otherProps}>
      <Heart
        className={`${inverted ? "inverted" : ""} ${
          toggleLikedItem ? "toggle-liked-item" : ""
        } heart-icon`}
      />
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  toggleLikedItem: selectLikedItemToggle,
});

export default connect(mapStateToProps)(LikedIcon);
