import React, { useState } from "react";
import "./seller.styles.scss";

import CustomButton from "../../components/custom-button/custom-button.component";
import FormInput from "../../components/form-input/form-input.component";
import CollectionItem from "../../components/collection-item/collection-item.component";
import { selectCartItems } from "../../redux/cart/cart.selectors";
import { useField, useFormik } from "formik";
import * as Yup from "yup";

const Seller = () => {
  const TextInputField = (props) => {
    const [field, meta] = useField(props);

    return (
      <>
        <FormInput {...field} {...props} />
        {meta.touched && meta.error ? <div>{meta.error}</div> : null}
      </>
    );
  };

  const yupSchema = Yup.object({
    itemName: Yup.string()
      .max(30, "Must be at most 30 characters")
      .required("Required"),
    price: Yup.number()
      .required("Required")
      .integer("Must be an integer")
      .positive("Must be a positive integer"),
    imageUrl: Yup.string().url("Must be URL").required("Required"),
  });

  const formik = useFormik({
    initialValues: { itemName: "", price: "", imageUrl: "" },
    validationSchema: yupSchema,
    onSubmit: (values, { setSubmitting }) => {
      setTimeout(() => {
        console.log("Seller form Submitted: ", JSON.stringify(values, null, 2));
        setSubmitting(false);
      }, 1500);
    },
  });

  return (
    <div className="seller-page-setup">
      <div className="seller-form">
        <h2 className="title">
          I want to upload my products on Crown Clothing
        </h2>
        {/* TODO: Connect this feature to Firestore and set it live */}
        <span>Fill the form below below to upload you items</span>

        <form className="sign-up-form" onSubmit={formik.handleSubmit}>
          <FormInput
            id="itemName"
            name="itemName"
            type="text"
            {...formik.getFieldProps("itemName")}
            label="Item Name"
          />
          {formik.touched.itemName && formik.errors.itemName ? (
            <div>{formik.errors.itemName}</div>
          ) : null}

          <FormInput
            id="price"
            name="price"
            type="number"
            {...formik.getFieldProps("price")}
            label="price"
          />
          {formik.touched.price && formik.errors.price ? (
            <div>{formik.errors.price}</div>
          ) : null}

          <FormInput
            id="imageUrl"
            name="imageUrl"
            type="url"
            {...formik.getFieldProps("imageUrl")}
            label="Image Url"
          />
          {formik.touched.imageUrl && formik.errors.imageUrl ? (
            <div>{formik.errors.imageUrl}</div>
          ) : null}

          <div className="group">
            <select id="collection" name="collection" className="form-input">
              <option value="">Select a collection</option>
              <option value="Hats">Hats</option>
              <option value="Jackets">Jackets</option>
              <option value="Sneakers">Sneakers</option>
              <option value="Womens">Womens</option>
              <option value="Mens">Mens</option>
            </select>
          </div>

          <div className="seller-form-buttons">
            <CustomButton type="submit">PREVIEW</CustomButton>
            <CustomButton type="submit">UPLOAD</CustomButton>
          </div>
        </form>
      </div>
      {/* <div className="new-items">
        {newItems.map((item) => (
          <CollectionItem item={item} />
        ))}
      </div> */}
    </div>
  );
};

export default Seller;
