import React from "react";
import "./liked-icon.styles.scss";
import { ReactComponent as Heart } from "../../assets/heart.svg";
import { toggleLikedHidden } from "../../redux/liked/liked.actions";

const LikedIcon = ({ inverted, toggleLikedHidden }) => {
  return (
    <div className="liked-icon" onClick={() => toggleLikedHidden}>
      <Heart className={`${inverted ? "inverted" : ""} heart-icon`} />
    </div>
  );
};

const mapDispatchToProps = (dispatch) => ({
  toggleLikedHidden: () => dispatch(toggleLikedHidden()),
});

export default LikedIcon;
