const INITIAL_STATE = {
  hiddenLiked: true,
  likedItems: [],
};

const likedReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "TOGGLE_LIKED_HIDDEN":
      return {
        ...state,
        hiddenLiked: !state.hiddenLiked,
      };
    case "ADD_LIKED_ITEM":
      return {
        ...state,
        likedItems: [...state.liked.likedItems, action.payload],
      };
    default:
      return state;
  }
};

export default likedReducer;
