import React, { Component } from "react";
import "./seller.styles.scss";

import CustomButton from "../../components/custom-button/custom-button.component";
import FormInput from "../../components/form-input/form-input.component";
import CollectionItem from "../../components/collection-item/collection-item.component";
import { selectCartItems } from "../../redux/cart/cart.selectors";

const Seller = () => {
  this.state = {
    name: "",
    price: "",
    imageUrl: "",
    collection: "",
    newItems: [],
  };

  //LEARN: revisit how to implement this method.
  const handleSubmit = (event) => {
    event.preventDefault();
    const { name, price, imageUrl, collection, newItems } = this.state;

    console.log("seller form submitted");
  };

  const handleChange = (event) => {
    const { name, value } = event.target;

    this.setState({ [name]: value });
  };

  const { name, price, imageUrl, collection } = this.state;
  return (
    <div className="seller-page-setup">
      <div className="seller-form">
        <h2 className="title">
          I want to upload my products on Crown Clothing
        </h2>
        {/* TODO: Connect this feature to Firestore and set it live */}
        <span>Fill the form below below to upload you items</span>
        <form className="sign-up-form" onSubmit={this.handleSubmit}>
          <FormInput
            type="text"
            name="name" //this is the prop in the object.
            value={name}
            handleChange={this.handleChange}
            label="name"
            required
          />
          <FormInput
            type="number"
            name="price"
            value={price}
            handleChange={this.handleChange}
            label="price"
            required
          />
          <FormInput
            type="url"
            name="imageUrl"
            value={imageUrl}
            handleChange={this.handleChange}
            label="Image Url"
            // required
          />
          <FormInput
            type="select"
            name="collection"
            value={collection}
            handleChange={this.handleChange}
            label="Collection"
            // required
          />
          <select id="collection" name="collection">
            <option value="Hats">Hats</option>
            <option value="Jackets">Jackets</option>
            <option value="Sneakers">Sneakers</option>
            <option value="Womens">Womens</option>
            <option value="Mens">Mens</option>
          </select>
          <div className="seller-form-buttons">
            <CustomButton type="submit">PREVIEW</CustomButton>
            <CustomButton type="submit">UPLOAD</CustomButton>
          </div>
        </form>
      </div>
      <div className="new-items">
        {this.state.newItems.map((item) => (
          <CollectionItem item={item} />
        ))}
      </div>
    </div>
  );
};

export default Seller;
